(function($) { 
    $(function() { 
  
      //  abrir e fechar nav
      $('#navbar-toggle').click(function() {
        $('nav ul').slideToggle();
      });
  
  
      // Alternar hambúrguer
      $('#navbar-toggle').on('click', function() {
        this.classList.toggle('active');
      });
  
  
      // Se um link tiver um menu suspenso, adicione alternância de submenu.
      $('nav ul li a:not(:only-child)').click(function(e) {
        $(this).siblings('.navbar-dropdown').slideToggle("slow");
  
        // Fechar a lista suspensa ao selecionar outra lista suspensa
        $('.navbar-dropdown').not($(this).siblings()).hide("slow");
        e.stopPropagation();
      });
  
  
      // Clique fora da lista suspensa removerá a classe suspensa
      $('html').click(function() {
        $('.navbar-dropdown').hide();
      });
    }); 
  })(jQuery); 


